
-- Criar tabela controle_clientes
CREATE TABLE public.controle_clientes (
  id uuid NOT NULL DEFAULT gen_random_uuid() PRIMARY KEY,
  tenant_id uuid NOT NULL REFERENCES public.tenants(id),
  cliente text,
  placa text,
  renavam text,
  cnh text,
  cpf_cnpj text,
  validade_cnh date,
  proximo_prazo text,
  obs text,
  ultima_consulta date,
  created_at timestamptz NOT NULL DEFAULT now(),
  updated_at timestamptz NOT NULL DEFAULT now()
);

-- RLS
ALTER TABLE public.controle_clientes ENABLE ROW LEVEL SECURITY;

CREATE POLICY "tenant_select" ON public.controle_clientes FOR SELECT USING (tenant_id = get_user_tenant_id());
CREATE POLICY "tenant_insert" ON public.controle_clientes FOR INSERT WITH CHECK (tenant_id = get_user_tenant_id());
CREATE POLICY "tenant_update" ON public.controle_clientes FOR UPDATE USING (tenant_id = get_user_tenant_id());
CREATE POLICY "tenant_delete" ON public.controle_clientes FOR DELETE USING (tenant_id = get_user_tenant_id());

-- Trigger updated_at
CREATE TRIGGER update_controle_clientes_updated_at
  BEFORE UPDATE ON public.controle_clientes
  FOR EACH ROW EXECUTE FUNCTION public.update_updated_at_column();

